(() => {
const casinoOptions = window.__OPTIONS__;

function removeRtpBlock(rules) {
	const rtpRegExp = /\{RTP[^}]*\}/;
	rules.querySelectorAll('h1, h2, h3, h4, h5, h6').forEach((h) => {
		let nextElem = h.nextElementSibling;
		let hasRtp = false;

		while (
			nextElem &&
			(rtpRegExp.test(nextElem.textContent) ||
				nextElem.matches('[class*="rtp-"]') ||
				nextElem.querySelector('[class*="rtp-"]'))
		) {
			hasRtp = true;
			const toRemove = nextElem;
			nextElem = nextElem.nextElementSibling;
			toRemove.remove();
		}

		if (hasRtp) {
			h.remove();
		}
	});
}

function filterRtpElements(rules, mathRtp, showRtp) {
	const RTP_REGEX_MAP = {
		main_rtp: /main/,
		chance_rtp: /chance/,
		bonus_rtp: /buy/
	};

	const isNeedRemoveRtp = Object.keys(RTP_REGEX_MAP).filter((rtp) => !showRtp.includes(rtp));

	rules.querySelectorAll('[class*="rtp-"]').forEach((element) => {
		const rtpName = element.className.replace('rtp-', '');
		if (!mathRtp?.[rtpName] || isNeedRemoveRtp.some((rtp) => RTP_REGEX_MAP[rtp]?.test(rtpName))) {
			element.remove();
		}
	});
}

function setRtpValues(rules, mathRtp) {
	if (mathRtp) {
		rules.innerHTML = rules.innerHTML.replaceAll(`{RTP_VALUE}`, `{RTP.main}`);
		Object.entries(mathRtp).forEach(([key, value]) => {
			rules.innerHTML = rules.innerHTML.replaceAll(`{RTP.${key}}`, value);
		});
	}
}

function handleRtp(options) {
	const { ui, math, license_rules } = options;
	const mathRtp = math?.rtp;
	const showRtp = license_rules?.rules_section?.show_rtp;
	const isNeedRemoveRtp = !showRtp?.length || ui?.show_rtp_in_rules === false;

	document.querySelectorAll('#rules').forEach((fragment) => {
		if (isNeedRemoveRtp) {
			removeRtpBlock(fragment);
		} else {
			const rtp = !isNaN(mathRtp) ? { main: mathRtp } : mathRtp;
			filterRtpElements(fragment, rtp, showRtp);
			setRtpValues(fragment, rtp);
		}
	});
}

handleRtp(casinoOptions);

})()

(() => {
const casinoOptions = window.__OPTIONS__;

function formatBet(bet, subunits, exponent = 0) {
	if (!bet) return '0';
	const betValue = bet / subunits;
	let ret;

	if (exponent > 0) {
		let str = betValue.toFixed(exponent).split('.');
		if (str[0].length > 3) {
			str[0] = str[0].replace(/(.)(?=(.{3})+$)/g, '$1,');
		}
		ret = str.join('.');
	} else {
		ret = betValue.toFixed(0).replace(/(.)(?=(.{3})+$)/g, '$1,');
	}
	return ret;
}

function multiplyValues(options) {
	const { rules } = options;
	const { currency } = rules || {};
	const { symbol, exponent, subunits } = currency || {};

	document.querySelectorAll('.multiply_value').forEach((fragment) => {
		const regex = /\{([^*]+)\*([^}]+)\}/;
		const match = fragment.textContent.match(regex);
		if (match) {
			const currencySymbol = symbol || '';
			const value = rules[match[1]] * +match[2];
			const formatedValue = formatBet(value, subunits, exponent);
			fragment.textContent = fragment.textContent.replace(match[0], `${formatedValue} ${currencySymbol}`);
		} else {
			fragment.remove();
		}
	});
}

multiplyValues(casinoOptions);

})()

(() => {
const casinoOptions = window.__OPTIONS__;

function removeRulesElement(className) {
	document.querySelectorAll(className).forEach((fragment) => {
		fragment.remove();
	});
}

function handleSlotsLicense(options) {
	const rulesSection = options?.license_rules?.rules_section;
	if (rulesSection?.show_license_requirements === true) {
		if (rulesSection?.license_requirement_show_purchase !== true) {
			removeRulesElement('.license-purchase');
		}
		if (rulesSection?.license_requirement_show_autoplay !== true) {
			removeRulesElement('.license-autoplay');
		}
		if (rulesSection?.license_requirement_show_quick_spins !== true) {
			removeRulesElement('.license-quick-spins');
		}
	} else {
		removeRulesElement('.license-block');
		}
}

handleSlotsLicense(casinoOptions);

})()

(() => {
const casinoOptions = window.__OPTIONS__;

const getDigitsCount = (val, subUnits) => {
  const numString = (val / subUnits).toString();

  if (numString.includes('e')) {
      const [coeff, exp] = numString.split('e');
      const decimalPart = coeff.split('.')[1] || '';
      return decimalPart.replace(/0+$/, '').length + Math.abs(+exp);
  }

  if (numString.includes('.')) {
      return numString.split('.')[1].replace(/0+$/, '').length;
  }

  return 0;
};

function formatBet(bet, subunits, exponent = 0) {
	const betValue = bet / subunits;
	let formatted;

	if (exponent > 0) {
		let [intPart, decimalPart] = betValue.toFixed(exponent).split('.');
		intPart = intPart.replace(/\B(?=(\d{3})+(?!\d))/g, ' ');
		formatted = decimalPart ? `${intPart}.${decimalPart}` : intPart;
	} else {
		formatted = betValue.toFixed(0).replace(/\B(?=(\d{3})+(?!\d))/g, ' ');
	}

	return formatted;
}

function setMinMaxBets(options) {
	const { rules = {}, license_rules = {} } = options || {};
	const { min_bet = 0, max_bet = 0, currency = {} } = rules;
	const { symbol = '', subunits = 1 } = currency;
	const isNeedToRemoveMinMaxBetBlock = license_rules?.rules_section?.show_min_max !== true || !min_bet || !max_bet;

	document.querySelectorAll('.min_max_bet').forEach((fragment) => {
		if (isNeedToRemoveMinMaxBetBlock) {
			fragment.remove();
		} else {
			const exponent = Math.max(getDigitsCount(min_bet, subunits), getDigitsCount(max_bet, subunits));

			const bets = {
				'{min_bet}': `${formatBet(min_bet, subunits, exponent)} ${symbol}`,
				'{max_bet}': `${formatBet(max_bet, subunits, exponent)} ${symbol}`
			};

			fragment.textContent = fragment.textContent.replace(/{min_bet}|{max_bet}/g, (match) => bets[match]);
		}
	});
}

setMinMaxBets(casinoOptions);

})()

