(() => {
const casinoOptions = window.__OPTIONS__;

function handleCustomVariables(options) {
	const custom = options?.custom;
	if (custom) {
		document.querySelectorAll('#rules').forEach((fragment) => {
			if (custom.paytableFormatted) {
				Object.entries(custom.paytableFormatted).forEach(([key, value]) => {
					fragment.innerHTML = fragment.innerHTML.replaceAll(`{PAYTABLE.${key}}`, value);
				});
			}

			if (custom.wintableFormatted) {
				Object.entries(custom.wintableFormatted).forEach(([key, value]) => {
					fragment.innerHTML = fragment.innerHTML.replaceAll(`{WIN.${key}}`, value);
				});
			}

			if (custom.maxWinsFormatted) {
				custom.maxWinsFormatted.forEach((winFormatted, index) => {
					fragment.innerHTML = fragment.innerHTML.replaceAll(`{MAX_WIN.${index}}`, winFormatted);
				});
			}

			if (custom.betFormatted) {
				fragment.innerHTML = fragment.innerHTML.replaceAll(`{BET_FORMATTED}`, custom.betFormatted);
			}

			if (custom.fee) {
				fragment.innerHTML = fragment.innerHTML.replaceAll(`{COMMISSION}`, custom.fee);
			}
		});
	}
}

handleCustomVariables(casinoOptions);

})()

(() => {
const casinoOptions = window.__OPTIONS__;

function setMaxFreq(options) {
	const { license_rules, math } = options;
	const { max_multiplier, max_win_freq } = math || {};
	const brazilLicenseRequirement = license_rules?.license === 'brazilian' && max_win_freq <= 100000000;
	const isNeedToRemoveBlock =
		license_rules?.rules_section?.show_max_win_frequency !== true ||
		!max_multiplier ||
		!max_win_freq ||
		brazilLicenseRequirement;

	document.querySelectorAll('.max_freq').forEach((fragment) => {
		if (isNeedToRemoveBlock) {
			const parent = fragment.parentElement;
			fragment.remove();
			const parentContent = parent?.textContent;
			if (parent && parent.tagName === 'LI' && parent.childElementCount === 0 && parentContent.trim() === '') {
				parent.remove();
			}
		} else {
			fragment.textContent = fragment.textContent.replace('{max_multiplier}', `${max_multiplier}`);
			fragment.textContent = fragment.textContent.replace('{max_win_freq}', `${max_win_freq}`);
		}
	});
}

setMaxFreq(casinoOptions);

})()

